import './App.css';
import { useState, useEffect } from 'react';
import ArticleList from './components/ArticleList';
import Form from './components/Form';
import APIService from './components/APIService';

function App() {
  const [articles, setArticles] = useState([]);
  const [editedArticle, setEditedArticle] = useState(null);
  const [formType, setFormType] = useState(null); // Track form type: 'insert' or 'edit'

  useEffect(() => {
    fetch('http://192.168.0.240:8333/get', {
      method: 'GET',
      headers: { 'Content-Type': 'application/json' }
    })
      .then(resp => resp.json())
      .then(resp => setArticles(resp))
      .catch(error => console.log(error));
  }, []);

  // Insert a new article
  const openInsertForm = () => {
    setEditedArticle({ title: '', body: '' }); // Empty fields for new article
    setFormType('insert');
  };

  // Edit an existing article (First one for now)
  const openEditForm = (article) => {
    setEditedArticle(article); // Set selected article for editing
    setFormType('edit');
  };

  // Update an existing article
  const updatedData = (article) => {
    const newArticles = articles.map(myArticle =>
      myArticle.id === article.id ? article : myArticle
    );
    setArticles(newArticles);
    setFormType(null); // Close form after update
  };

  // Insert a new article
  const insertedArticle = (article) => {
    setArticles([...articles, article]);
    setFormType(null); // Close form after insertion
  };

  return (
    <div className="App">
      <h1>Hello again React</h1>
      <div className="row">
        <div className="col">
          <button className="btn btn-success" onClick={openInsertForm}>
            Insert Article
          </button>
        </div>
        <div className="col">
          <button className="btn btn-primary" onClick={() => openEditForm(articles[0])}>
            Edit Article
          </button>
        </div>
      </div>

      {/* Insert/Edit Form (Conditional Rendering) */}
      {formType === 'insert' || formType === 'edit' ? (
        <Form 
          article={editedArticle} 
          updatedData={updatedData} 
          insertedArticle={insertedArticle} 
        />
      ) : null}

      {/* Display the Article List */}
      <ArticleList articles={articles} editArticle={openEditForm} deleteArticle={setArticles} />
    </div>
  );
}

export default App;
