import React, { useEffect } from 'react';
import grapesjs from 'grapesjs';
import 'grapesjs/dist/css/grapes.min.css';

function DragDropEditor() {
    useEffect(() => {
        grapesjs.init({
            container: '#editor',
            fromElement: true,
            width: '100%',
            height: '600px',
            storageManager: false,
            panels: { defaults: [] },
            blockManager: {
                appendTo: '#blocks',
                blocks: [
                    {
                        id: 'button',
                        label: 'Button',
                        content: '<button>Click Me</button>',
                        category: 'Basic',
                    },
                    {
                        id: 'text',
                        label: 'Text',
                        content: '<p>Hello World!</p>',
                        category: 'Basic',
                    }
                ],
            }
        });
    }, []);

    return (
        <div>
            <div id="blocks"></div>
            <div id="editor"></div>
        </div>
    );
}

export default DragDropEditor;
