import './App.css';
import { useState, useEffect } from 'react';
import ArticleList from './components/ArticleList';
import Form from './components/Form';

function App() {
  const [articles, setArticles] = useState([]);
  const [editedArticle, setEditedArticle] = useState(null);

  // Fetch articles from backend
  useEffect(() => {
    fetch('http://192.168.0.240:8333/get', {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
      },
    })
      .then((resp) => resp.json())
      .then((resp) => setArticles(resp))
      .catch((error) => console.log(error));
  }, []);

  // Edit an article
  const editArticle = (article) => {
    setEditedArticle(article);
  };

  // Update existing article
  const updatedData = (article) => {
    const new_articles = articles.map((my_article) =>
      my_article.id === article.id ? article : my_article
    );
    setArticles(new_articles);
  };

  // Open an empty form to insert a new article
  const openForm = () => {
    setEditedArticle({ title: '', body: '' });
  };

  // Insert a new article
  const insertedArticle = (article) => {
    setArticles([...articles, article]);
  };

  // Delete an article
  const deleteArticle = (article) => {
    const new_articles = articles.filter((my_article) => my_article.id !== article.id);
    setArticles(new_articles);
  };

  return (
    <div className="App">
      <div className="row">
        <div className="col">
          <h1>Hello again React</h1>
        </div>
        <div className="col">
          <button className="btn btn-success" onClick={openForm}>
            Insert Article
          </button>
        </div>
      </div>

      {/* Article List Component */}
      <ArticleList articles={articles} editArticle={editArticle} deleteArticle={deleteArticle} />

      {/* Form Component (Conditional Rendering) */}
      {editedArticle && (
        <Form article={editedArticle} updatedData={updatedData} insertedArticle={insertedArticle} />
      )}
    </div>
  );
}

export default App;
